import { useState } from "react";

function App() {
  const [news, setNews] = useState("");
  const [result, setResult] = useState(null);
  const [label, setLabel] = useState(null); // ‚úÖ Ï∂îÍ∞Ä
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");
  const [prob, setProb] = useState(null); // ‚úÖ ÌôïÎ•† Ï†ÄÏû•Ïö©

  // üîß ÏûÖÎ†• ÌíàÏßà Í≤ÄÏÇ¨ Ìï®Ïàò (Ïª¥Ìè¨ÎÑåÌä∏ Ïô∏Î∂ÄÏóê Ï†ïÏùò)
function getInputQualityScore(text) {
  const cleaned = text.replace(/\s+/g, ""); // Í≥µÎ∞± Ï†úÍ±∞
  const words = text.split(/\s+/).filter((w) => w.length > 0);
  const uniqueWords = new Set(words);
  const uniqueChars = new Set(cleaned);

  const length = cleaned.length;
  const wordRatio = uniqueWords.size / words.length;
  const charRatio = uniqueChars.size / cleaned.length;

  const hasMeaninglessPattern =
  /(„Öã„Öã+|„Öé„Öé+|„Ö†„Ö†+|„Öú„Öú+|zz+|„Ñ∑„Ñ∑+|„Ñ≤„Ñ≤+|www+|ÏùëÏï†+|„Ö°„Ö°+|asdf+|[a-zA-Z]{12,}|[!@#$%^&*()_+=\-{}[\]:;"'<>,.?/~`\\|]{4,}){2,}/.test(text);

  // Í∏∞Ï§ÄÏπò Ï°∞Í±¥
  const isTooShort = length < 50;
  const isTooLong = length > 3000;
  const isTooRepetitive =
  (words.length >= 50 && wordRatio < 0.5) || // Í∏∏Ïù¥Í∞Ä Ï∂©Î∂ÑÌï† ÎïåÎßå ÎπÑÏú® Ï†ÅÏö©
  (cleaned.length >= 50 && charRatio < 0.2); // ÎÑàÎ¨¥ ÏßßÏùÄ ÏûÖÎ†•ÏùÄ Î¨¥Ïãú
  const isGibberish = hasMeaninglessPattern;

  const qualityIssues = [];

  if (isTooShort) qualityIssues.push("");
  if (isTooLong) qualityIssues.push("");
  if (isTooRepetitive) qualityIssues.push("‚ö†Ô∏è Î∞òÎ≥µ Îã®Ïñ¥/Î¨∏ÏûêÍ∞Ä Í≥ºÎèÑÌïòÍ≤å ÎßéÏäµÎãàÎã§.");
  if (isGibberish) qualityIssues.push("‚ö†Ô∏è ÏùòÎØ∏ ÏóÜÎäî Ìå®ÌÑ¥(ÌäπÏàòÎ¨∏Ïûê)Ïù¥ Ìè¨Ìï®ÎêòÏñ¥ ÏûàÏäµÎãàÎã§.");


  return {
    isValid: qualityIssues.length === 0,
    issues: qualityIssues
  };
}

  const analyzeNews = async () => {
    if (!news.trim()) {
      setErrorMsg("‚ö†Ô∏è Îâ¥Ïä§ Í∏∞ÏÇ¨Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî!");
      return;
    }

    setLoading(true);
    setErrorMsg("");
    setResult(null);
    setLabel(null); // Ï¥àÍ∏∞Ìôî

    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text: news }),
      });
      const data = await response.json();
      if (data.error) {
      setErrorMsg("‚ö†Ô∏è Î¨∏Ïû•ÏùÑ Ïù¥Ìï¥Ìï† Ïàò ÏóÜÏñ¥ Î∂ÑÏÑùÌï† Ïàò ÏóÜÏäµÎãàÎã§.");
      setLoading(false);
      setProb(null); // ‚úÖ ÌôïÎ•† Ï¥àÍ∏∞Ìôî Ï∂îÍ∞Ä
      return;
    }
      setProb(data.probabilities);  // ‚Üê ÌôïÎ•† Î∞õÏïÑÏÑú ÏÉÅÌÉú Ï†ÄÏû•
      setResult(data.result);   // ÏÑ§Î™Ö Î¨∏Íµ¨ (ex: "üî¥ Í∞ÄÏßú Îâ¥Ïä§Î°ú ÌåêÎã®Îê®")
      setLabel(data.label);     
    } catch (error) {
      console.error("ÏóêÎü¨ Î∞úÏÉù:", error);
      setErrorMsg("ÏÑúÎ≤ÑÏôÄ Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî.");
      setProb(null); // ‚úÖ ÌôïÎ•† Ï¥àÍ∏∞Ìôî Ï∂îÍ∞Ä
    } finally {
      setLoading(false);
    }
  };

  const quality = errorMsg === "ÏÑúÎ≤ÑÏôÄ Ïó∞Í≤∞Ìï† Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî."
  ? { isValid: true, issues: [] }
  : getInputQualityScore(news);


  return (
    <div
      style={{
        minHeight: "100vh",
        background: "linear-gradient(to bottom, #f0f4ff, #ffffff)",
        fontFamily: "'Roboto', sans-serif",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <header
        style={{
          backgroundColor: "#0056b3",
          padding: "20px 40px",
          color: "white",
          fontSize: "24px",
          fontWeight: "bold",
          boxShadow: "0 2px 10px rgba(0,0,0,0.2)",
          textAlign: "center",
        }}
      >
        üß† AI Îâ¥Ïä§ ÏßÑÏúÑ ÌåêÎ≥Ñ ÏãúÏä§ÌÖú
      </header>

      <main
        style={{
          flex: 1,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          padding: "40px 20px",
        }}
      >
        <div
          style={{
            maxWidth: "700px",
            width: "100%",
            backgroundColor: "white",
            padding: "40px",
            borderRadius: "16px",
            boxShadow: "0 6px 20px rgba(0,0,0,0.1)",
            textAlign: "center",
          }}
        >
          <h1 style={{ fontSize: "36px", color: "#333", marginBottom: "20px" }}>
            üì∞ Îâ¥Ïä§ Ïã†Î¢∞ÎèÑ Î∂ÑÏÑù
          </h1>
          <p style={{ color: "#666", fontSize: "16px", marginBottom: "30px" }}>
            Îâ¥Ïä§ Í∏∞ÏÇ¨ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÎ©¥ AIÍ∞Ä ÏßÑÏßúÏù∏ÏßÄ ÌåêÎ≥ÑÌï¥ ÎìúÎ¶ΩÎãàÎã§.
          </p>

          <textarea
            value={news}
            onChange={(e) => setNews(e.target.value)}
            placeholder="Îâ¥Ïä§ Í∏∞ÏÇ¨Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî(50 ~ 1500Ïûê). . ."
            rows="8"
            style={{
              width: "100%",
              padding: "18px",
              fontSize: "16px",
              border: "1px solid #ccc",
              borderRadius: "10px",
              resize: "none",
              boxSizing: "border-box",
              marginBottom: "30px",
              fontFamily: "'Roboto', sans-serif",
              color: "#333",
              backgroundColor: "#f9f9f9",
            }}
          ></textarea>
          <p 
          style={{ 
            fontSize: "13px", 
            color: "#888", 
            marginTop: "-20px", 
            marginBottom: "20px"
             }}>
          ({news.length} / 3000Ïûê)
          </p>

